package TwoPointer;

public class ValidePalindrome2 {

    public boolean optimal(String s){
        int start = 0;
        int end = s.length()-1;

        while (start < end){
            if(s.charAt(start) != s.charAt(end)){
                var removeLeftChar = isPalindrome(s,start +1,end);
                var removeRightChar = isPalindrome(s,start,end-1);
                if(removeLeftChar || removeRightChar) return  true;
                else  return  false;

            }
            start++;
            end--;
        }
        return true;
    }

    public  boolean brute(String s){
        for(int i=0;i<s.length();i++){
            var newString = s.substring(0,i) + s.substring(i+1,s.length());
//            System.out.println(newString);
            if(isPalindrome(newString, 0 , s.length()-1)) return true;
        }
        return  false;

    }

    public boolean isPalindrome(String s ,int start , int end){

        while(start < end){
            if(s.charAt(start++)!= s.charAt(end--)) return false;
        }
        return  true;
    }


    static void main() {
        var s1 = "aba";
        var s2 = "";
        var s3 = "a";
        var s4 = "aaba";
        var s5 = "lclc";
        System.out.println("output");
        var vp = new ValidePalindrome2();
//        System.out.println(vp.brute(s2));
        System.out.println(vp.optimal(s2));
    }
}
